static double equation1(int a, int b, int c) { 

        if (a > 0 && b > 0) {

            return Math.cbrt(5+c*Math.sqrt(b+5*Math.sqrt(a)));
        }

        System.out.println("а і b мають бути більше 0!");

        return 0.0;

 }

static double equation2(int k, int d){

        double result;

        if (k>10){

            result = Math.sqrt(k*Math.sqrt(d*d) + d*Math.sqrt(k*k));

        }

        else result = (k+d)*(k+d);

        return result;

    }

static double equation3(int[] A, int[] B, int n){

        double f;

        if (A.length == B.length && A.length == n){

            double f1 = 1.0;

            double f2 = 0.0;

            int i = 0;

            while (i < n){

                f1 *= (A[i]+B[i+1]);

                f2 += (A[i+1]*B[i]);

                f = f1 + f2

                System.out.println(f);

                i++;

        }

        else System.out.println("Розмір масиву не дорівнює n!");

        return f;

    }
